        default:
            tai = "unknow"
            break
    }
}

def getCgiValue(String rattype, String ltv_userlocationinformation) {
    switch (rattype) {
        case "06":
        case "08":
            if (ltv_userlocationinformation.size() > 25) {
                def location = ltv_userlocationinformation[12..17].toUpperCase()
                def str1 = location.substring(0, 2).reverse()
                def thirdDigit = location[3]
                def mcc = str1 + thirdDigit
                def mnc = ''
                if (location[2]=='F'){
                    mnc = location.substring(4, 6).reverse()
                } else {
                    mnc = location.substring(4, 6).reverse() + location[2]

                }
                def hexDec1 = ltv_userlocationinformation[19..23]
                def hexTratado1 = String.format("%05d", Integer.parseInt(hexDec1, 16))
                if (hexTratado1.length() < 7) {
                    def value = 7 - hexTratado1.length()
                    for (i = 0; i < value; i++) {
                        hexTratado1 = "0" + hexTratado1
                    }
                }
                def hexDec2 = ltv_userlocationinformation[24..25]
                def hexTratado2 = String.format("%02d", Integer.parseInt(hexDec2, 16))
                if (hexTratado2.length() < 3) {
                    def value = 3 - hexTratado2.length()
                    for (i = 0; i < value; i++) {
                        hexTratado2 = "0" + hexTratado2
                    }
                }
                cgi = "${mcc}-${mnc}-${hexTratado1}-${hexTratado2}"
            } else {
                cgi = ""
            }
            break
        case "01":
        case "02":
            def location = ltv_userlocationinformation[2..7].toUpperCase()
            def str1 = location.substring(0, 2).reverse()
            def thirdDigit = location[3]
            def mcc = str1 + thirdDigit
            def mnc = ''
            if (location[2]=='F'){
                mnc = location.substring(4, 6).reverse()
            } else {
                mnc = location.substring(4, 6).reverse() + location[2]

            }
            def hexDec1 = ltv_userlocationinformation[8..11]
            def hexTratado1 = String.format("%05d", Integer.parseInt(hexDec1, 16))
            if (hexTratado1.length() < 5) {
                def value = 5 - hexTratado1.length()
                for (i = 0; i < value; i++) {
                    hexTratado1 = "0" + hexTratado1
                }
            }
            def hexDec2 = ltv_userlocationinformation[12..15]
            def hexTratado2 = String.format("%05d", Integer.parseInt(hexDec2, 16))
            if (hexTratado2.length() < 3) {
                def value = 3 - hexTratado2.length()
                for (i = 0; i < value; i++) {
                    hexTratado2 = "0" + hexTratado2
                }
            }
            cgi = "${mcc}-${mnc}-${hexTratado1}-${hexTratado2}"
            break
        case "10":
            def location = ltv_userlocationinformation[18..23].toUpperCase()
            def str1 = location.substring(0, 2).reverse()
            def thirdDigit = location[3]
            def mcc = str1 + thirdDigit
            def mnc = ''
            if (location[2]=='F'){
                mnc = location.substring(4, 6).reverse()
            } else {
                mnc = location.substring(4, 6).reverse() + location[2]

            }
            def hexDec1 = ltv_userlocationinformation[25..30]
            def hexDec2 = ltv_userlocationinformation[32..33]

            def decimalValue1 = Long.parseLong(hexDec1, 16)
            def binaryValue1 = "000" + Long.toString(decimalValue1, 2) + "00"
            binaryValue1 = binaryValue1.padLeft(26, '0')
            def decimalResult1 = Long.parseLong(binaryValue1, 2)
            def hexTratado1 = decimalResult1.toString().padLeft(8, '0')

            def decimalValue2 = Long.parseLong(hexDec2, 16)
            def binaryValue2 = Long.toString(decimalValue2, 2)
            //def lsb = (decimalValue & 0b11) // Pegando os 2 bits LSB
            binaryValue2 = "100" + binaryValue2.toString()
            def decimalResult = Long.parseLong(binaryValue2, 2)
            def hexTratado2 = decimalResult.toString().padLeft(4, '0')
            cgi = "${mcc}-${mnc}-${hexTratado1}-${hexTratado2}"
            break
        default:
            cgi = ""
            break
    }
}

